mapper.select:
select rank
from (
select local, y2023,
rank() over (order by y2023 desc) as rank
from perceivedsafety )
where local in (
select
local from gu_name where guname = #{guNameValue});

select rank
from (
select local, #{year}, 
rank() over (order by #{year} desc) as rank
from perceivedsafety )
where local in (
select
local from gu_name where guname = #{guNameValue});
